import{_ as t,c as i,o,ag as a}from"./chunks/framework.C48t5LJr.js";const p=JSON.parse('{"title":"About the Project","description":"","frontmatter":{},"headers":[],"relativePath":"en/guide/index.md","filePath":"en/guide/index.md","lastUpdated":1742438047000}'),r={name:"en/guide/index.md"};function n(l,e,s,d,u,c){return o(),i("div",null,e[0]||(e[0]=[a('<h1 id="about-the-project" tabindex="-1">About the Project <a class="header-anchor" href="#about-the-project" aria-label="Permalink to &quot;About the Project&quot;">​</a></h1><h2 id="what-is-lingyaokeys" tabindex="-1">What is LingYaoKeys? <a class="header-anchor" href="#what-is-lingyaokeys" aria-label="Permalink to &quot;What is LingYaoKeys?&quot;">​</a></h2><p>LingYaoKeys is a modern key tool developed based on .NET8.0 and WPF, offering rich features and an elegant user interface. The tool is designed with MVVM architecture and supports global hotkeys, side button and scroll wheel triggers, independent key intervals, coordinate movement, and more.</p><h2 id="system-requirements" tabindex="-1">System Requirements <a class="header-anchor" href="#system-requirements" aria-label="Permalink to &quot;System Requirements&quot;">​</a></h2><ul><li>Windows 10/11 recommended (Windows 7 has not been tested and may cause unexpected issues)</li><li>Run the program as administrator (for driver installation)</li><li>.NET 8.0 Runtime environment (if you get the prompt <code>You must install .NET Desktop Runtime to run this application.</code> when running the program, please download: <a href="https://download.visualstudio.microsoft.com/download/pr/64760cc4-228f-48e4-b57d-55f882dedc69/b181f927cb937ef06fbb6eb41e81fbd0/windowsdesktop-runtime-8.0.14-win-x64.exe" target="_blank" rel="noreferrer">.NET 8.0 Runtime</a>)</li></ul><h2 id="main-features" tabindex="-1">Main Features <a class="header-anchor" href="#main-features" aria-label="Permalink to &quot;Main Features&quot;">​</a></h2><h3 id="basic-features" tabindex="-1">Basic Features <a class="header-anchor" href="#basic-features" aria-label="Permalink to &quot;Basic Features&quot;">​</a></h3><ul><li>Support for global hotkey registration</li><li>Support for sequence/press hotkey mode switching</li><li>Support for side button and scroll wheel triggers</li><li>Support for mouse movement to corresponding coordinates, with coordinate input and editing capabilities</li><li>Independent intervals for each key and coordinate</li><li>Support for window handle detection, when enabled hotkeys only trigger for the corresponding window</li><li>Voice prompt toggle, volume settings, customizable key on/off voice prompts</li><li>Normal/Reduce Sticking mode toggle switch</li><li>Keys and coordinates support drag-and-drop sorting</li><li>Floating window display showing key start/stop/disabled status</li><li>Support for input method toggle, automatically switches to ENG output when hotkeys are triggered</li><li>Support for configuration export/import, online updates, debug mode, and more</li></ul><h3 id="driver-features" tabindex="-1">Driver Features <a class="header-anchor" href="#driver-features" aria-label="Permalink to &quot;Driver Features&quot;">​</a></h3><ul><li>Implemented using kernel-level DeviceIoControl driver</li><li>Support for offline operation</li><li>Support for 32-bit/64-bit system architectures</li><li>Support for USB/PS2 keyboard and mouse devices</li><li>Compatible with Win10/Win11 systems</li><li>Support for driver hot-swapping, traceless uninstallation when the program exits</li></ul><h2 id="technology-stack" tabindex="-1">Technology Stack <a class="header-anchor" href="#technology-stack" aria-label="Permalink to &quot;Technology Stack&quot;">​</a></h2><ul><li>Development Language: C#</li><li>Framework: .NET 8.0</li><li>UI Framework: WPF</li><li>Architecture Pattern: MVVM</li><li>Driver Development: Windows Driver Kit (WDK)</li><li>Build Tool: Visual Studio 2022</li></ul>',12)]))}const m=t(r,[["render",n]]);export{p as __pageData,m as default};
