import{_ as l,c as a,o as e,ag as t}from"./chunks/framework.C48t5LJr.js";const f=JSON.parse('{"title":"关于项目","description":"","frontmatter":{},"headers":[],"relativePath":"guide/index.md","filePath":"guide/index.md","lastUpdated":1742438047000}'),o={name:"guide/index.md"};function r(n,i,d,s,u,h){return e(),a("div",null,i[0]||(i[0]=[t('<h1 id="关于项目" tabindex="-1">关于项目 <a class="header-anchor" href="#关于项目" aria-label="Permalink to &quot;关于项目&quot;">​</a></h1><h2 id="什么是灵曜按键" tabindex="-1">什么是灵曜按键？ <a class="header-anchor" href="#什么是灵曜按键" aria-label="Permalink to &quot;什么是灵曜按键？&quot;">​</a></h2><p>灵曜按键是一个基于.NET8.0和WPF开发的现代化按键工具，它提供了丰富的功能和优雅的用户界面。该工具采用MVVM架构设计，支持全局热键、侧键滚轮触发、按键独立间隔、坐标移动等功能。</p><h2 id="系统要求" tabindex="-1">系统要求 <a class="header-anchor" href="#系统要求" aria-label="Permalink to &quot;系统要求&quot;">​</a></h2><ul><li>推荐Windows 10/11（Windows 7 未经过测试，可能导致无法预知的问题）</li><li>以管理员身份运行程序（用于驱动安装）</li><li>.NET 8.0 Runtime 环境 （如果运行程序提示<code>You must install .NET Desktop Runtime to run this application.</code>，请下载：<a href="https://download.visualstudio.microsoft.com/download/pr/64760cc4-228f-48e4-b57d-55f882dedc69/b181f927cb937ef06fbb6eb41e81fbd0/windowsdesktop-runtime-8.0.14-win-x64.exe" target="_blank" rel="noreferrer">.NET 8.0 Runtime</a>）</li></ul><h2 id="主要特性" tabindex="-1">主要特性 <a class="header-anchor" href="#主要特性" aria-label="Permalink to &quot;主要特性&quot;">​</a></h2><h3 id="基础功能" tabindex="-1">基础功能 <a class="header-anchor" href="#基础功能" aria-label="Permalink to &quot;基础功能&quot;">​</a></h3><ul><li>支持全局热键注册</li><li>支持顺序/按压热键模式切换</li><li>支持侧键和滚轮触发</li><li>支持鼠标移动至对应坐标，可进行坐标录入和编辑</li><li>每个按键及坐标设有独立间隔</li><li>支持窗口句柄嗅探，开启后仅对应窗口可触发热键</li><li>语音提醒开关，音量大小设置，自定义按键开启/按键关闭语音</li><li>正常/降低卡位模式切换开关</li><li>按键和坐标支持拖拽排序</li><li>浮窗置顶显示按键启动/关闭/禁用状态</li><li>支持输入法切换开关，开启后触发热键自动切换ENG输出</li><li>支持配置导出/导入、联网更新、调试模式等功能</li></ul><h3 id="驱动特性" tabindex="-1">驱动特性 <a class="header-anchor" href="#驱动特性" aria-label="Permalink to &quot;驱动特性&quot;">​</a></h3><ul><li>基于DeviceIoControl内核级驱动实现</li><li>支持离线运行</li><li>支持32位/64位系统架构</li><li>支持USB/PS2键鼠设备</li><li>兼容Win10/Win11系统</li><li>支持驱动热插拔，程序退出无痕卸载</li></ul><h2 id="技术栈" tabindex="-1">技术栈 <a class="header-anchor" href="#技术栈" aria-label="Permalink to &quot;技术栈&quot;">​</a></h2><ul><li>开发语言：C#</li><li>框架：.NET 8.0</li><li>UI框架：WPF</li><li>架构模式：MVVM</li><li>驱动开发：Windows Driver Kit (WDK)</li><li>构建工具：Visual Studio 2022</li></ul>',12)]))}const m=l(o,[["render",r]]);export{f as __pageData,m as default};
